    <script>$(function() {

            var regObj = new RegExp("[^0-9]", "g");
            var MonTime = 12;
            function Mon() {
                var Money = ($(".stages-poinput").val() / MonTime) + ($(".stages-poinput").val() * 0.0799 * (MonTime / 12) / MonTime);
                $(".stages-point-poshow span.jg").html(Money.toFixed(2));
            }
            $(".stages-poinput").change(function() {
                Mon();
            }); $(".stages-point-main p").click(function() {
                var index = $(this).index() + 1;
                MonTime = index * 12;
                $(".stages-point-main").removeClass("bg1").removeClass("bg2").removeClass("bg3").addClass("bg" + index);
                Mon();

            }); $(".stages-poinput").keydown(function() {
                $(this).val($(this).val().replace(regObj, ""));
                Mon();

            }); $(".stages-poinput").keyup(function() {
                $(this).val($(this).val().replace(regObj, ""));
                Mon();
            })
