<script type="text/javascript">//增加延迟加载的距离
    $("img").lazyload({
        threshold: 500
    });
    /*星星*/
    function star() {
        var timer = null;
        clearInterval(timer);
        timer = setInterval(function() {
            $(".header").find(".star-con").stop(false, true).fadeOut(500,
            function() {
                $(this).stop(false, true).fadeIn(500)
            })
        },
        1000);
    }
    star();
    //change
    var $part_change = $(".part_change");
    $part_change.each(function(index) {
        var partIndex = index + 2;
        var $descirption = $(this).find(".descirption");
        var $leftArr = $(this).find(".left-arr");
        var $rightArr = $(this).find(".right-arr");
        var $arr = $(this).find(".arr");
        var $deploy = $(this).find(".deploy");
        var $changeImg = $(this).find(".change-img").find("img");
        var $imgLen = $changeImg.length;
        var timer = null;
        var num = 0;
        //重复部分抽取
        function changeImg(num) {
            $changeImg.eq(num - 1).addClass("current").show().siblings("img").removeClass("current").hide();
        }
        //轮播
        function changeSports() {
            num++;
            num = num > $imgLen ? 0 : num;
            if (num == 0) {
                $changeImg.hide();
            } else {
                $changeImg.show();
                changeImg(num);
            }
        }
        clearInterval(timer);
        timer = setInterval(changeSports, 2000);
        //悬停轮播图
        function stopChange($obj) {
            $obj.on("touchstart",
            function() {
                clearInterval(timer);
            });
            $obj.on("touchend",
            function() {
                clearInterval(timer);
                timer = setInterval(changeSports, 2000);
            });
        }
        stopChange($(this));
        //点击左箭头
        $leftArr.on("click",
        function() {
            num--;
            num = num < 0 ? $imgLen: num;
            if (num == 0) {
                $changeImg.hide();
            } else {
                $changeImg.show();
                changeImg(num);
            }
        });
        //点击右箭头
        $rightArr.on("click",
        function() {
            changeSports();
        });
        //显示隐藏配置文字层deploy
        $descirption.on("click",
        function() {
            if ($deploy.css("display") == "none") {
                $deploy.show();
                clearInterval(timer);
            } else {
                $deploy.hide();
                clearInterval(timer);
                timer = setInterval(changeSports, 2000);
            }
        });
        $deploy.on("click",
        function() {
            $(this).hide();
            clearInterval(timer);
            timer = setInterval(changeSports, 2000);
        });
    });

