<script type="text/javascript">$(function() {
        //点击按钮弹出报名框
        $('.alert').on('click',
        function() {
            $('.tckbg').show();
            $('.apply').show();
        });
        //弹出报名框关闭按钮
        $('.sqk_alert_close').on('click',
        function() {
            $('.tckbg').hide();
            $('.apply').hide();
        });
        //图片轮播
        var timer = null;
        var i = 0;
        function turn(a, c) {
            a.eq(c).fadeIn(500).siblings().fadeOut(500);

        }
        function sport() {
            i++;
            if (i > 1) {
                i = 0;
            }
            turn($('.part3 .pig li'), i);
            turn($('.part4 .pig li'), i);
            turn($('.part5 .pig li'), i);
            turn($('.part6 .pig li'), i);
            turn($('.part7 .pig li'), i);
        }
        timer = setInterval(sport, 2000);
        //弹框表单
        var tim1 = null,
        lock1 = 1;
        $('#btn_appsubmit1').click(function() {
            var name1 = $('#app_name1').val();
            var tel1 = $('#app_tel1').val();
            var pattern = /^[\u4e00-\u9fa5 a-zA-Z0-9]{2,}$/;
            if (!pattern.test(name1)) {
                alert('亲,姓名格式有误');
                return;
            }
            if (name1 == '' || name1 == '您的姓名') {
                alert('亲,姓名必须填写。');
                return;
            }
            pattern = /(^((\+?86)|(\(\+86\)))?\d{3,4}-{1}\d{7,8}(-{0,1}\d{3,4})?$)|(^((\+?86)|(\(\+86\)))?1\d{10}$)/;
            if (!pattern.test(tel1)) {
                alert('亲,手机号码错了哦。');
                return;
            }
            if (lock1 == 1) {
                lock1 = 0;
                var $data = {name:name1,mobile:tel1,address_sheng:'',address_shi:'',needtype:'', needother:''};
                signupApply($data);
                //$('#m_wx_form_80').submit();
                tim1 = setTimeout(function() {
                    lock1 = 1;
                },
                3000)
            } else {
                return false;
            }
        });
    });</script>
